$(function () {

    $('.slider').slick({
        prevArrow: '<button type="button" class="slick-btn slick-prev"><img src="images/main/slider/arrow-next.png" alt=""></button>',
        nextArrow: '<button type="button" class="slick-btn slick-next"><img src="images/main/slider/arrow-prev.png" alt=""></button>',
        dots: true,
    })

    $('.accordion__wrapper-heading').click(function () {
        $(this).toggleClass('in').next().slideToggle();
        $('.accordion__wrapper-heading').not(this).removeClass('in').next().slideUp();
    });

    const parallaxElements = document.querySelectorAll('.parallax')


    const parallax = elements => {
        if ('undefined' !== elements && elements.length > 0) {
            elements.forEach(element => {
                let y = window.innerHeight - element.getBoundingClientRect().top
                if (y > 0) {
                    element.style.transform = 'translate3d(0, -' + (0.15 * y) + 'px ,0)'
                }
            })
        }
    }

    parallax(parallaxElements)

    window.onscroll = () => {
        parallax(parallaxElements)
    }
});